library(coda)
library(truncnorm)
library(EpiEstim)

##model function
fpredict<-function(t,params,states,Nts)
{
  Nt<-Nts
  a<-params[1]
  b<-params[2]
  c<-params[3]
  w<-params[4]
  L<-params[5]
  D<-params[6]
  alpha<-params[7]
  miu<-params[8]
  temp<-matrix(0,nrow=t,ncol=1)
  beta<-matrix(0,nrow=t,ncol=1)
  rate<-matrix(0,nrow=t,ncol=1)
  S<-matrix(0,nrow=t,ncol=1)
  I<-matrix(0,nrow=t,ncol=1)
  R<-matrix(0,nrow=t,ncol=1)
  V<-matrix(0,nrow=t,ncol=1)
  P<-matrix(0,nrow=7,ncol=1)
  Rt<-matrix(0,nrow=t,ncol=1)
  S[1,]<-states[1]
  I[1,]<-states[2]
  R[1,]<-states[3]
  rate[1,]<-states[4]
  V[1,]<-states[5]
  
  for (i in 1:t){
    beta[i,]<-a*(1+b*sin(w*i+c))
  }
  for (i in 1:7){
    P[i,]<-dgamma(i,4.05,1.5)
  }
  for(i in 2:7)
  {
    temp[1,]<-(beta[1,]*S[1,]*P[1,]*I[1,])/Nt
    temp[2,]<-(beta[1,]*S[1,]*(P[1,]*I[1,]+P[2,]*I[1,]))/Nt
    temp[3,]<-(beta[2,]*S[2,]*(P[1,]*I[2,]+P[2,]*I[1,]+P[3,]*I[1,]))/Nt
    temp[4,]<-(beta[3,]*S[3,]*(P[1,]*I[3,]+P[2,]*I[2,]+P[3,]*I[1,]+P[4,]*I[1,]))/Nt
    temp[5,]<-(beta[4,]*S[4,]*(P[1,]*I[4,]+P[2,]*I[3,]+P[3,]*I[2,]+P[4,]*I[1,]+P[5,]*I[1,]))/Nt
    temp[6,]<-(beta[5,]*S[5,]*(P[1,]*I[5,]+P[2,]*I[4,]+P[3,]*I[3,]+P[4,]*I[2,]+P[5,]*I[1,]+P[6,]*I[1,]))/Nt
    temp[7,]<-(beta[6,]*S[6,]*(P[1,]*I[6,]+P[2,]*I[5,]+P[3,]*I[4,]+P[4,]*I[3,]+P[5,]*I[2,]+P[6,]*I[1,]+P[7,]*I[1,]))/Nt
    
    
    I[i,]<-I[i-1,]+temp[i-1,]-I[i-1,]/D
    R[i,]<-R[i-1,]+I[i-1,]/D-((Nt-S[i-1,]-I[i-1,]-V[i-1,])/L)
    S[i,]<-S[i-1]+((Nt-S[i-1,]-I[i-1,]-V[i-1,])/L)-temp[i-1,]
    V[i,]<-Nt-S[i-1]-R[i-1,]-I[i-1,]
    rate[i,]<-temp[i,]/(Nt*0.1)
    
  }
  
  for(i in 8:t)
  { 
    temp[i,]<-(beta[i-1,]*S[i-1,]*(P[1,]*I[i-1,]+P[2,]*I[i-2,]+P[3,]*I[i-3,]+P[4,]*I[i-4,]+P[5,]*I[i-5,]+P[6,]*I[i-6,]+P[7,]*I[i-7,]))/Nt
    
    I[i,]<-I[i-1,]+temp[i-1,]-I[i-1,]/D
    R[i,]<-R[i-1,]+I[i-1,]/D-((Nt-S[i-1,]-I[i-1,]-V[i-1,])/L)
    S[i,]<-S[i-1]+((Nt-S[i-1,]-I[i-1,]-V[i-1,])/L)-temp[i-1,]
    V[i,]<-Nt-S[i-1]-R[i-1,]-I[i-1,]
    rate[i,]<-temp[i,]/(Nt*0.1)
  }
  res<-cbind(S,I,R,rate,V)
  return(res)
  
}
##Loss function

logloss<-function(rate,params,states,Nts)
{
  nt<-length(rate)
  loss<-0
  
  
  pstates<-fpredict(nt,params,states,Nts) 
  
  
  for (i in 1:nt)
  {
    loss<-loss+abs(rate[i]-(pstates[i,4]))*1000
  }
  
  loss<-(-1*loss)
  return(loss)}



