###18-19 MCMC
for(i in 2:N)
{
  oparams<-mcparams[i-1,]
  for(j in c(1,2,3)){ 
    
    
    vi<-rnorm(1,mean=oparams[j],sd=sds[j])
    zj<-pnorm(vi,mean=oparams[j],sd=sds[j])
    if (j==2){
      vi<-rtruncnorm(1,a=0.05,b=0.3,mean=oparams[j],sd=sds[j])
      zj<-ptruncnorm(vi,a=0.05,b=0.3,mean=oparams[j],sd=sds[j])
    }
    if (j==5){
      vi<-rtruncnorm(1,a=365*2,b=365*5,mean=oparams[j],sd=sds[j])
      zj<-ptruncnorm(vi,a=365*2,b=365*5,mean=oparams[j],sd=sds[j])
    }
    if (j==6){
      vi<-rtruncnorm(1,a=3,b=10,mean=oparams[j],sd=sds[j])
      zj<-ptruncnorm(vi,a=3,b=10,mean=oparams[j],sd=sds[j])
    }
    nparams<-oparams
    nparams[j]<-vi
    lgloss1<-0
    lgloss2<-0
    for(sy in 1:ny) 
    {
      ytemp<-ally[data$`Epidemiological.year`==syear[sy]]
      lgloss1<-lgloss1+logloss(ytemp,oparams,states0,Nts)+log(zj)
      lgloss2<-lgloss2+logloss(ytemp,nparams,states0,Nts)+log(zj)
    }
    ratio<-exp(lgloss2-lgloss1)
    if(j==2){
      ratio=ratio/(pnorm((0.3-vi)/sds[j],0,1)-pnorm((0.05-vi)/sds[j],0,1))
      ratio=ratio*(pnorm((0.3-oparams[j])/sds[j],0,1)-pnorm((0.05-oparams[j])/sds[j],0,1))
    }
    if(j==5){
      ratio=ratio/(pnorm((365*5-vi)/sds[j],0,1)-pnorm((365*2-vi)/sds[j],0,1))
      ratio=ratio*(pnorm((365*5-oparams[j])/sds[j],0,1)-pnorm((365*2-oparams[j])/sds[j],0,1))
    }
    if(j==6){
      ratio=ratio/(pnorm((10-vi)/sds[j],0,1)-pnorm((3-vi)/sds[j],0,1))
      ratio=ratio*(pnorm((10-oparams[j])/sds[j],0,1)-pnorm((3-oparams[j])/sds[j],0,1))
    }
    ratio<-min(1,ratio)
    if(is.nan(ratio))ratio<-0
    if(runif(1)<=ratio){
      oparams<-nparams
      accept[j]<-accept[j]+1
    } 
  }
  mcparams[i,]=oparams
  setTxtProgressBar(pb,i)
}

