mcparams[,6]<-Eparams[6]
mcparams[,7]<-Eparams[7]
mcparams[,8]<-Eparams[8]
### initial 
mcparams[1,]<-c(0.7,Eparams[2],Eparams[3],0.017,Eparams[5],5,0.6,0.1) 

pb <- txtProgressBar(min=2,max=N,style = 4)   
accept<-rep(0,npams) 

sds<-c(0.003,0.005,0.05,0.0001,15,0.01)

###21 MCMC
for(i in 2:N)
{
  oparams<-mcparams[i-1,]
  for(j in c(1,3)){ 
    
    
    vi<-rnorm(1,mean=oparams[j],sd=sds[j])
    zj<-pnorm(vi,mean=oparams[j],sd=sds[j])
    nparams<-oparams
    nparams[j]<-vi
    lgloss1<-0
    lgloss2<-0
    for(sy in 1:ny) 
    {
      ytemp<-ally[data$`Epidemiological.year`==syear[sy]]
      lgloss1<-lgloss1+logloss(ytemp,oparams,states3,Nts)+log(zj)
      lgloss2<-lgloss2+logloss(ytemp,nparams,states3,Nts)+log(zj)
    }
    ratio<-exp(lgloss2-lgloss1)
    ratio<-min(1,ratio)
    if(is.nan(ratio))ratio<-0
    if(runif(1)<=ratio){
      oparams<-nparams
      accept[j]<-accept[j]+1
    } 
  }
  mcparams[i,]=oparams
  setTxtProgressBar(pb,i)
}

accept=accept/N
cat("\n Accept Rates:", accept)

mcsample<-(N/2):N
bparams<-mcparams[mcsample,]
Eparams21<-apply(bparams,2,mean)   
HPDIs<-HPDinterval(as.mcmc(bparams[,])) 


op <- par(no.readonly = T)
par(mfcol=c(2,1))
for(i in c(1,3)){
  plot(bparams[,i],xlab='Iteration',type='l',ylab=parNames[i])
  abline(h=Eparams21[i],col='red')
}
par(op)

cat("\n Estimate with HPDI: \n")  
for(i in 1:npams)
{
  text=sprintf("%s:%.5f[%.5f,%.5f]\n",parNames[i],Eparams21[i],HPDIs[i,1],HPDIs[i,2])
  cat(text)
}   
cat("\n")

